mapper.select:
	SELECT 
 
	FROM sysitem_search_log WHERE 1=1
	
		 AND search_time > #{startTime} 
	
	
		 AND search_time < #{endTime} 
	
	
		AND type = #{type}
  
	
		AND keyword LIKE CONCAT('%',#{keyword},'%')
  
  	ORDER BY search_time DESC
	
		LIMIT #{offset},#{pageSize}

	SELECT COUNT(*) FROM sysitem_search_log WHERE 1=1
	
		 AND search_time > #{startTime} 
	
	
		 AND search_time < #{endTime} 
	
	
		AND keyword LIKE CONCAT('%',#{keyword},'%')
  
	
		AND type = #{type}

	SELECT * FROM 
	(SELECT log_id, keyword, COUNT(*) num, st.type
	FROM
	  (SELECT * FROM sysitem_search_log 
	  WHERE search_time 
	  
			 AND search_time > #{startTime} 
		
		
			 AND search_time < #{endTime} 
		
	  ORDER BY search_time) st 
	WHERE 1=1
	
		AND type = #{type}
  
	
		AND keyword LIKE CONCAT('%',#{keyword},'%')
  
	GROUP BY st.keyword 
	ORDER BY num DESC) aa
	
		WHERE log_id = #{logId}
  
	
		LIMIT #{offset},#{pageSize}

SELECT COUNT(*) FROM ( SELECT keyword, COUNT(*) num, st.type FROM (SELECT * FROM sysitem_search_log WHERE search_time
AND search_time > #{startTime}
AND search_time < #{endTime}
ORDER BY search_time) st WHERE 1=1
AND type = #{type}
AND keyword LIKE CONCAT('%',#{keyword},'%')
GROUP BY st.keyword ORDER BY num DESC ) tb

mapper.update:
    	UPDATE sysitem_search_log
			
         
            keyword =#{keyword},
         
         
             ip =#{ip},
         
         
             result_num =#{resultNum}
         
         
             type =#{type}
         
         
             search_time =#{searchTime}
         
      
    	WHERE log_id = #{logId}
