mapper.select:
SELECT m.*
FROM ms_account_detail m
WHERE m.business_no=#{orderId}

select m.id, m.account_no accountNo, m.trade_type tradeType, m.trade_amount tradeAmount, DATE_FORMAT(m.trade_date,'%Y-%m-%d %H:%i:%s') tradeDate, m.in_or_out inOrOut, m.balance, m.business_no businessNo, m.create_user create_user, DATE_FORMAT(m.create_date,'%Y-%m-%d %H:%i:%s') create_date, m.update_user updateUser, DATE_FORMAT(m.update_date,'%Y-%m-%d %H:%i:%s') updateDate, m.remark, ma.mem_id memId from ms_account_detail m left join ms_account ma on m.account_no = ma.account_no where 1 = 1
and ma.mem_id = #{memId}
and m.trade_type in
#{id}
and m.business_no = #{businessNo}

select mad.id, mad.account_no accountNo, mad.trade_type tradeType, mad.trade_amount tradeAmount, DATE_FORMAT(mad.trade_date,'%Y-%m-%d %H:%i:%s') tradeDate, mad.in_or_out inOrOut, mad.balance, mad.business_no businessNo, mad.create_user create_user, DATE_FORMAT(mad.create_date,'%Y-%m-%d %H:%i:%s') create_date, mad.update_user updateUser, DATE_FORMAT(mad.update_date,'%Y-%m-%d %H:%i:%s') updateDate, mad.remark, mm.mem_login_name memLoginName, mm.mem_phone memPhone, md.dict_value bizType from ms_account_detail mad left join ms_account ma on ma.account_no = mad.account_no left join ms_members mm on mm.mem_id = ma.mem_id left join ms_dict md on md.dict_id = mad.trade_type where 1 = 1
and mm.mem_login_name = #{memLoginName}
and mm.mem_phone = #{memPhone}
and mad.trade_type = #{tradeType}
and mad.create_date >= #{beginDate}
and mad.create_date >= #{beginDate} and #{endDate} >= mad.create_date
