mapper.update:
update ms_bank_account
   set update_date = now()
,account_idcard = #{account_idcard}
,account_no = #{account_no}
,account_name = #{account_name}
,account_bank = #{account_bank}
,account_province = #{account_province}
,account_city = #{account_city}
,account_area = #{account_area}
,account_sub_bank = #{account_sub_bank}
,is_default = #{is_default}
,remark = #{remark}
 where id = #{id}
