mapper.update:
update ms_bank_withdraw_deposit
   set status = #{status},
       audit_by = #{auditBy},
       audit_date = #{auditDate},
       audit_state = #{auditState}
 where id = #{id}

mapper.select:
select a.id as id, a.business_no as businessNo, a.mem_id as memId, a.bank_account_id as bankAccountId, a.account_idcard as accountIdcard, a.bank_card_no as bankCardNo, a.account_name as accountName, a.account_bank as accountBank, a.account_province as accountProvince, a.account_city as accountCity, a.account_area as accountArea, a.account_sub_bank as accountSubBank, apply_withdraw_amount as applyWithdrawAmount, poundage_amount as poundageAmount, actual_withdraw_amount as actualWithdrawAmount, a.apply_date as applyDate, a.audit_by as auditBy, a.audit_date as auditDate, a.status as status, a.audit_state as auditState, a.remark as remark, a.create_date as createDate, b.mem_phone as phone, b.mem_login_name as loginName, a.actual_transfer_cash as actualTransferCash, a.withdraw_balance as withdrawBalance /**提现时余额 */ from ms_bank_withdraw_deposit a LEFT JOIN ms_members b on b.mem_id=a.mem_id
a.is_delete='N'
and a.id = #{id}
and b.mem_login_name = #{memLoginName}
and b.mem_phone = #{memPhone}
and a.business_no like CONCAT('%',#{businNo},'%')
and a.status =#{status}
and a.apply_date >=#{beginDate}
and #{endDate} >= a.apply_date

SELECT msa.id as id, msa.account_no as accountNo , msa.mem_id as memId , msa.account_type_no as accountTypeNo, msa.account_no_sequence as accountNoSequence, msa.balance as balance , msa.balance_encrypt as balanceEncrypt , msa.freeze_balance as freezeBalance, msa.freeze_balance_encrypt as freezeBalanceEncrypt, msa.withdraw_poundage_scale as withdrawPoundageScale FROM ms_account msa
AND msa.account_status=0 AND msa.allow_withdraw=0 /**账号状态正常 并是可提现的 */
AND msa.id = #{id}
AND msa.mem_id = #{memId}
AND msa.account_no = #{accountNo}
ORDER BY msa.withdraw_priority DESC /**优先级排序 1表示提现扣款 2表示驳回倒序退 */
ORDER BY msa.withdraw_priority ASC

mapper.update:
UPDATE ms_account
SET freeze_balance =freeze_balance + #{freezeBalance},update_date = now()


        and id = #{id}


        and account_no = #{accountNo}

UPDATE ms_account
SET balance =#{balance},update_date = now()


        and id = #{id}


        and account_no = #{accountNo}

UPDATE ms_account_report
SET freeze_balance =freeze_balance + #{freezeBalance},update_date = now()
WHERE mem_id = #{memId}

UPDATE ms_account_report
SET balance =balance + #{balance},update_date = now()
WHERE mem_id = #{memId}
