mapper.select:
select mer.rec_id recId, mte.enterprise_key enterpriseKey, mted.det_id detId, mer.order_id orderId, mer.mem_id memId, mer.phone phone, mer.extorder_id extorderId, mer.extcompany_code extCompanyCode, mte.enterprise_name companyName, mer.account_type accountType, mted.enterprise_account_name accountName, mer.recharge_amout rechargeAmout, mer.recharge_date rechargeDate, mer.reality_recharge_date realityRechargeDate, mer.status status, mer.remark remark, mer.callback_url callbackUrl from ms_tripartite_recharge mer,ms_tripartite_enterprise mte,ms_tripartite_enterprise_detail mted WHERE mte.enterprise_identity = mer.extcompany_code AND mted.ent_id = mte.ent_id and mted.personal_account_type = mer.account_type
AND mer.rec_id=#{recId}
AND mer.phone=#{phone}
AND mer.order_id=#{orderId}
AND mer.extorder_id=#{extorderId}
AND mer.status=#{status}
AND mer.recharge_date >= #{rechargeTimeBegin}
AND mer.recharge_date <= #{rechargeTimeEnd}
AND mer.reality_recharge_date >= #{realityRechargeTimeBegin}
AND mer.reality_recharge_date <= #{realityRechargeTimeEnd}

mapper.update:
update ms_tripartite_recharge
status = #{status,jdbcType=CHAR}, reality_recharge_date = #{realityRechargeDate}, callback_qty = callback_qty+1
AND rec_id=#{recId}

mapper.select:
select t.cash_account+t.line_of_credit totalAmount,t.cash_account cashAccount
	 from ms_tripartite_enterprise_detail t
	 where t.det_id=#{detId}

select mta.flow_id flowId, mta.flow_type flowType, mta.extcompany_code extcompanyCode, mta.account_type accountType, mte.enterprise_name extcompanyName, mted.enterprise_account_name accountName, mta.before_change_amout beforeChangeAmout, mta.after_change_amout afterChangeAmout, mta.income income, mta.expenses expenses, mta.order_id orderId, mta.created_date createdDate, mta.remark remark, mta.created_by createdBy from ms_tripartite_account_flow mta,ms_tripartite_enterprise mte,ms_tripartite_enterprise_detail mted WHERE mte.enterprise_identity = mta.extcompany_code and mte.ent_id = mted.ent_id and mta.account_type = mted.personal_account_type
AND mted.enterprise_account_name = #{enterpriseAccountName}
AND mta.extcompany_code = #{extcompanyCode}
AND mta.created_date >= #{flowTimeBegin}
AND mta.created_date <= #{flowTimeEnd}
